// Scales
import { pxToRem } from '../../utils/scales';

export const theme = {
    colors: {
        primary: "#54a0ff"
    },
    paddings: {
        md: `${pxToRem(10)} ${pxToRem(20)}`
    },
    rounded: {
        medium: "5px"
    }
}
